require('dotenv').config();
const jwt = require('jsonwebtoken');

const isAuthenticated = (req, res, next) => {
    const protectedRoutes = ['/', '', 'index', 'profile', 'chat', 'api'];
    const apiRoute= "api";
    const publicAPI = ['login'];
    const url = req.url;
    const urlPath = url.split('/')[1];

    if (!protectedRoutes.includes(urlPath)) {
        return next();
    }
    if (urlPath === apiRoute && publicAPI.includes(url.split(/[/]+/).pop())) {
        return next();
    }
    if (!req.get('Authorization')) {
        console.log(req.url);
        return res.redirect('/login');
    }
    let token = req.headers['Authorization']; // obtain from post in request
    jwt.verify(token, process.env.JWT_SIGNATURE, function (err, decoded) {
        if (err || decoded == undefined) {
            console.log(err);
            return res.redirect('/login');
        }
        return next()
    });
};

module.exports = isAuthenticated;